mapper.select:
SELECT IR.REQEST_SEQNO , IR.REQEST_DEPT_CODE , SII.INSPCT_INSTT_NM AS REQEST_DEPT_CODE_NM , IR.CLIENT_ID , SU.USER_NM AS CLIENT_NM , IR.REQEST_DTE , IR.REQEST_NO , IR.INSPCT_TY_CODE , SCC1.CMMN_CODE_NM AS INSPCT_TY_CODE_NM , IR.CUSTLAB_SEQNO , RC.CUSTLAB_NM , SCC2.CMMN_CODE_NM AS PRDUCT_SE_CODE_NM , IR.MTRIL_SEQNO , SM.MTRIL_NM , IR.MNFCTUR_DTE , IR.SPLORE_NM , IR.ELCTC_QY , IR.ELCTC_CN , SE.ENTRPS_NM , IR.PRDUCT_DC , IR.RM , IR.PROGRS_SITTN_CODE , SCC3.CMMN_CODE_NM AS PROGRS_SITTN_CODE_NM , IR.JDGMNT_WORD_CODE FROM IM_REQEST IR JOIN SY_INSPCT_INSTT SII on IR.REQEST_DEPT_CODE = SII.INSPCT_INSTT_CODE JOIN SY_USER SU ON SU.USER_ID = IR.CLIENT_ID AND SU.USE_AT = 'Y' AND SU.DELETE_AT = 'N' LEFT JOIN SY_CMMN_CODE SCC1 ON SCC1.CMMN_CODE = IR.INSPCT_TY_CODE AND SCC1.USE_AT = 'Y' LEFT JOIN RS_CUSTLAB RC ON IR.CUSTLAB_SEQNO = RC.CUSTLAB_SEQNO AND RC.USE_AT = 'Y' AND RC.DELETE_AT = 'N' JOIN SY_MTRIL SM ON IR.MTRIL_SEQNO = SM.MTRIL_SEQNO LEFT JOIN SY_CMMN_CODE SCC2 ON SCC2.CMMN_CODE = SM.PRDUCT_SE_CODE LEFT JOIN SY_ENTRPS SE ON SE.ENTRPS_SEQNO = IR.ENTRPS_SEQNO LEFT JOIN SY_CMMN_CODE SCC3 ON SCC3.CMMN_CODE = IR.PROGRS_SITTN_CODE AND SCC3.USE_AT = 'Y' WHERE SII.DELETE_AT = 'N' AND SII.USE_AT = 'Y' AND IR.BPLC_CODE = AND IR.PROGRS_SITTN_CODE = #{progrsSittnCode}
AND IR.CUSTLAB_SEQNO = #{custlabSeqno}
AND SM.MTRIL_NM LIKE '%' || TRIM(#{mtrilNm}) || '%'
AND IR.REQEST_NO LIKE '%' || TRIM(#{reqestNo}) || '%'
AND IR.INSPCT_TY_CODE = #{inspctTyCode}
AND IR.REQEST_DTE BETWEEN #{reqestBeginDte} AND #{reqestEndDte}
AND IR.MNFCTUR_DTE BETWEEN #{mnfcturBeginDte} AND #{mnfcturEndDte}
ORDER BY IR.MNFCTUR_DTE, IR.REQEST_NO

WITH T AS (SELECT IRE.*, IR.MTRIL_SEQNO, SM.MTRIL_NM, SU.UNIT_NM, SE.EXPRIEM_NM, RE.EQPMN_NM, IR.ENTRPS_SEQNO, IR.INSPCT_TY_CODE, IR.REQEST_NO FROM IM_REQEST IR LEFT JOIN IM_REQEST_EXPRIEM IRE ON IRE.REQEST_SEQNO = IR.REQEST_SEQNO JOIN SY_EXPRIEM SE ON IRE.EXPRIEM_SEQNO = SE.EXPRIEM_SEQNO LEFT JOIN SY_UNIT SU ON IRE.UNIT_SEQNO = SU.UNIT_SEQNO LEFT JOIN RS_EQPMN RE ON IRE.EQPMN_SEQNO = RE.EQPMN_SEQNO LEFT JOIN SY_MTRIL SM ON SM.MTRIL_SEQNO = IR.MTRIL_SEQNO WHERE 1=1 AND IRE.DELETE_AT = 'N' AND SE.EXPRIEM_CL_CODE = 'SY05000001' AND IR.VENDOR_REQEST_AT = 'N' AND IR.REQEST_SEQNO = #{reqestSeqno}
AND IRE.PROGRS_SITTN_CODE = #{progrsSittnCode}
) SELECT T.BPLC_CODE , T.INSPCT_TY_CODE , T.REQEST_SEQNO , T.ENTRPS_SEQNO , T.MTRIL_SEQNO , T.MTRIL_NM , T.REQEST_NO , T.REQEST_EXPRIEM_SEQNO , T.PROGRS_SITTN_CODE , IRER.EXPR_ODR , CASE WHEN T.LAST_EXPR_ODR = IRER.EXPR_ODR THEN 'Y' ELSE 'N' END LAST_EXPR_ODR_AT , T.MTRIL_SDSPC_SEQNO , T.EXPRIEM_NM , T.EXPRIEM_SEQNO , T.SDSPC_NM , T.PROGRS_SITTN_CODE , T.LCL_VALUE || ' ~ ' || T.UCL_VALUE AS LCL_UCL , IRER.RESULT_VALUE , IRER.CALC_LAW_SEQNO , IRER.NOMFRM_CN , IRER.VRIABL_ID , IRER.NOMFRM_NM , IRER.RVSOP_CN , IRER.MARK_CPHR , IRER.RVSOP_CPHR_RANDOM_CREAT_AT , IRER.RDMS_DOC_NO , IRER.RDMS_REGIST_AT , TO_CHAR(TO_DATE(T.LAST_RESULT_REGIST_DTE), 'YYYY-MM-DD') AS RESULT_REGIST_DTE , T.UNIT_NM , T.UNIT_SEQNO , T.EQPMN_NM , T.EQPMN_SEQNO , T.JDGMNT_FOM_CODE , T.LCL_VALUE , T.LCL_VALUE_SE_CODE , T.UCL_VALUE , T.UCL_VALUE_SE_CODE , T.TEXT_STDR , IRER.DELETE_AT , IRER.LAS_REGIST_AT , IRER.JDGMNT_WORD_CODE FROM T JOIN ( SELECT IIRER.*, INV.RDMS_UUID NOMFRM_UUID, INV.UPDT_RDMS_DOC_NO NOMFRM_RDMS_DOC_NO FROM IM_REQEST_EXPRIEM_RESULT IIRER LEFT JOIN IM_REQEST_EXPRIEM_VRIABL INV ON INV.REQEST_EXPRIEM_SEQNO = IIRER.REQEST_EXPRIEM_SEQNO AND INV.EXPR_ODR = IIRER.EXPR_ODR AND INV.VRIABL_ID = IIRER.VRIABL_ID JOIN IM_REQEST_EXPRIEM IIRE ON IIRE.REQEST_EXPRIEM_SEQNO = IIRER.REQEST_EXPRIEM_sEQNO WHERE IIRER.DELETE_AT = 'N' AND IIRE.REQEST_SEQNO = #{reqestSeqno} ) IRER ON T.REQEST_EXPRIEM_SEQNO = IRER.REQEST_EXPRIEM_SEQNO
AND T.LAST_EXPR_ODR = IRER.EXPR_ODR
WHERE 1 = 1 ORDER BY IRER.EXPR_ODR DESC, T.SORT_ORDR

mapper.update:
		BEGIN
			UPDATE IM_REQEST_EXPRIEM SET
				PROGRS_SITTN_CODE = 'IM03000003'
			WHERE REQEST_SEQNO = #{reqestSeqno} AND DELETE_AT = 'N';

           	UPDATE IM_REQEST IR SET
           		IR.PROGRS_SITTN_CODE = (SELECT MIN(IRE.PROGRS_SITTN_CODE) FROM IM_REQEST_EXPRIEM IRE WHERE IRE.REQEST_SEQNO = IR.REQEST_SEQNO)
           	WHERE IR.REQEST_SEQNO = #{reqestSeqno}
           	AND IR.DELETE_AT = 'N';
		END;

mapper.select:
SELECT
	*
FROM RS_NCR_EXPRIEM A
WHERE A.REQEST_EXPRIEM_SEQNO = #{reqestExpriemSeqno}
AND A.EXPR_ODR = #{exprOdr}
