mapper.select:
SELECT A.MHRLS_EDAY_CHCK_SEQNO ,A.YEAR ,A.QU ,A.INSPCT_CRRCT_NM ,A.INSPCT_CRRCT_SE_CODE ,A.REGISTER_ID ,A.DELETE_AT ,A.LAST_CHANGER_ID ,A.LAST_CHANGE_DT ,A.ATCHMNFL_SEQNO ,(SELECT XA.CMMN_CODE_NM FROM SY_CMMN_CODE XA WHERE XA.CMMN_CODE = A.INSPCT_CRRCT_SE_CODE) AS CRRCTCODENM ,A.MHRLS_SEQNO ,B.MHRLS_NM as inMhrlsNm ,B.DETECT_LIMIT_APPLC_AT AS detectLimitApplcAt ,B.MHRLS_CL_CODE FROM RS_MHRLS_EDAY_CHCK A LEFT JOIN RS_MHRLS B ON A.MHRLS_SEQNO = B.MHRLS_SEQNO WHERE A.DELETE_AT ='N'
AND A.INSPCT_CRRCT_SE_CODE = #{inspctCrrctSeCodeSch}
AND UPPER(A.INSPCT_CRRCT_NM) LIKE '%'||UPPER(#{inspctCrrctNmSch})||'%'
AND A.YEAR = #{yearSch}
AND A.QU = #{quSch}
AND UPPER(B.MHRLS_NM ) LIKE '%'||UPPER(#{mhrlsNmSch})||'%'

SELECT A.REQEST_SEQNO, A.LOT_ID, LISTAGG(D.EXPRIEM_SEQNO, '/') WITHIN GROUP(ORDER BY A.LOT_ID, B.SORT_ORDR, B.SDSPC_NM, C.EXPR_ODR, C.EXPR_NUMOT) EXPRIEM_SEQNO, LISTAGG(B.REQEST_EXPRIEM_SEQNO, '/') WITHIN GROUP(ORDER BY A.LOT_ID, B.SORT_ORDR, B.SDSPC_NM, C.EXPR_ODR, C.EXPR_NUMOT) REQEST_EXPRIEM_SEQNO, LISTAGG(D.EXPRIEM_NM || CASE WHEN NVL(B.SDSPC_NM,'N') != 'N' THEN '('|| B.SDSPC_NM || ')' END, '/') WITHIN GROUP(ORDER BY A.LOT_ID, B.SORT_ORDR, B.SDSPC_NM, C.EXPR_ODR, C.EXPR_NUMOT) EXPRIEM_NM, LISTAGG(NVL(C.RESULT_VALUE, ' '), '/') WITHIN GROUP(ORDER BY A.LOT_ID, B.SORT_ORDR, B.SDSPC_NM, C.EXPR_ODR, C.EXPR_NUMOT) RESULT_VALUE, REGEXP_REPLACE(LISTAGG(NVL(E.USER_NM, '-'), ',') WITHIN GROUP(ORDER BY E.USER_NM, A.LOT_ID, B.SORT_ORDR, C.EXPR_ODR, C.EXPR_NUMOT), '([^,]+)(,\1)+', '\1') RESULT_REGISTER_NM, C.EXPR_NUMOT, B.SDSPC_NM, C.EXPR_ODR FROM IM_REQEST A INNER JOIN IM_REQEST_EXPRIEM B ON A.REQEST_SEQNO = B.REQEST_SEQNO INNER JOIN IM_REQEST_EXPRIEM_RESULT C ON B.REQEST_EXPRIEM_SEQNO = C.REQEST_EXPRIEM_SEQNO AND B.LAST_EXPR_ODR = C.EXPR_ODR INNER JOIN SY_EXPRIEM D ON B.EXPRIEM_SEQNO = D.EXPRIEM_SEQNO LEFT JOIN SY_USER E ON E.USER_ID = B.RESULT_REGISTER_ID WHERE A.REQEST_SEQNO IN
#{reqestSeqno}
AND B.DELETE_AT = 'N' AND C.DELETE_AT = 'N' GROUP BY A.REQEST_SEQNO, A.LOT_ID, C.EXPR_NUMOT, B.SDSPC_NM, C.EXPR_ODR ORDER BY A.LOT_ID

SELECT A.YEAR || '년 '|| A.QU || '분기' AS YEAR , D.MHRLS_NM , SE.EXPRIEM_NM , ROUND(AVG(F.RESULT_VALUE),5) RESULT_VALUE_AVG FROM RS_MHRLS_EDAY_CHCK A INNER JOIN RS_MHRLS_EDAY_CHCK_REQEST B ON A.MHRLS_EDAY_CHCK_SEQNO = B.MHRLS_EDAY_CHCK_SEQNO INNER JOIN IM_REQEST C ON B.REQEST_SEQNO = C.REQEST_SEQNO INNER JOIN RS_MHRLS D ON C.MHRLS_SEQNO = D.MHRLS_SEQNO INNER JOIN IM_REQEST_EXPRIEM E ON C.REQEST_SEQNO = E.REQEST_SEQNO INNER JOIN SY_EXPRIEM SE ON SE.EXPRIEM_sEQNO = E.EXPRIEM_sEQNO LEFT JOIN IM_REQEST_EXPRIEM_RESULT_AVRG F ON F.REQEST_EXPRIEM_SEQNO = E.REQEST_EXPRIEM_SEQNO AND F.EXPR_ODR = E.LAST_EXPR_ODR WHERE A.DELETE_AT = 'N' AND D.MHRLS_CL_CODE IN ( SELECT ID.MHRLS_CL_CODE FROM RS_MHRLS_EDAY_CHCK IA INNER JOIN RS_MHRLS_EDAY_CHCK_REQEST IB ON IA.MHRLS_EDAY_CHCK_SEQNO = IB.MHRLS_EDAY_CHCK_SEQNO INNER JOIN IM_REQEST IC ON IB.REQEST_SEQNO = IC.REQEST_SEQNO INNER JOIN RS_MHRLS ID ON IC.MHRLS_SEQNO = ID.MHRLS_SEQNO WHERE IA.MHRLS_EDAY_CHCK_SEQNO = #{mhEdayChckSeq}
) AND SE.EXPRIEM_SEQNO = (SELECT EXPRIEM_SEQNO FROM IM_REQEST_EXPRIEM WHERE REQEST_EXPRIEM_SEQNO = #{reqestExpriemSeqno}) AND D.MHRLS_CL_CODE = #{mhrlsClCode}
AND D.MHRLS_SEQNO = #{mhrlsSeqno} GROUP BY A.YEAR , A.QU , D.MHRLS_NM , SE.EXPRIEM_NM
ORDER BY A.YEAR, A.QU
GROUP BY A.YEAR , A.QU , D.MHRLS_NM , SE.EXPRIEM_NM
ORDER BY mhrls_nm, A.YEAR, A.QU

mapper.insert:

	SELECT SQ_RS_MHRLS_EDAY_CHCK.NEXTVAL AS mhrlsEdayChckSeqno FROM DUAL 

INSERT INTO RS_MHRLS_EDAY_CHCK(
	MHRLS_EDAY_CHCK_SEQNO
	,YEAR
	,QU
	,INSPCT_CRRCT_NM
	,INSPCT_CRRCT_SE_CODE
	,REGISTER_ID
	,DELETE_AT
	,LAST_CHANGER_ID
	,LAST_CHANGE_DT
	,ATCHMNFL_SEQNO
	,MHRLS_SEQNO
	) VALUES (
	 #{mhrlsEdayChckSeqno}
	, #{year}
	, #{qu}
	, #{inspctCrrctNm}
	, #{inspctCrrctSeCode}
	, #{registerId}
	, 'N'
	, #{lastChangerId}
	, SYSDATE
	, #{atchmnflSeqno}
	, #{mhrlsSeqnon}
	)

mapper.update:
UPDATE RS_MHRLS_EDAY_CHCK SET
	 YEAR = #{year}
	, QU = #{qu}
	, INSPCT_CRRCT_NM = #{inspctCrrctNm}
	, INSPCT_CRRCT_SE_CODE = #{inspctCrrctSeCode}
	, REGISTER_ID = #{registerId}
	,LAST_CHANGER_ID		= #{lastChangerId}
	,LAST_CHANGE_DT			= SYSDATE
	,ATCHMNFL_SEQNO 		= #{atchmnflSeqno}
	,MHRLS_SEQNO	=#{mhrlsSeqnon}
WHERE MHRLS_EDAY_CHCK_SEQNO  = #{mhrlsEdayChckSeqno}

mapper.insert:
INSERT INTO RS_MHRLS_EDAY_CHCK_REQEST(
	MHRLS_EDAY_CHCK_SEQNO
	,REQEST_SEQNO
	,LAST_CHANGER_ID
	,LAST_CHANGE_DT
) VALUES (
	 #{mhrlsEdayChckSeqno}
	, #{reqestSeqno}
	, #{lastChangerId}
	, SYSDATE
)

mapper.update:
BEGIN
	UPDATE IM_REQEST SET
		 PROGRS_SITTN_CODE = 'IM03000006'
		 ,LAST_CHANGER_ID		= #{lastChangerId}
		 ,LAST_CHANGE_DT			= SYSDATE
	WHERE REQEST_SEQNO = #{reqestSeqno};

	UPDATE IM_REQEST_EXPRIEM SET
		PROGRS_SITTN_CODE = 'IM03000006'
		,LAST_CHANGER_ID		= #{lastChangerId}
		,LAST_CHANGE_DT			= SYSDATE
	WHERE REQEST_SEQNO = #{reqestSeqno}	;
END;

mapper.delete:
DELETE FROM RS_MHRLS_EDAY_CHCK_REQEST 
WHERE 1=1

	AND REQEST_SEQNO = #{reqestSeqno} 

AND MHRLS_EDAY_CHCK_SEQNO = #{mhrlsEdayChckSeqno}

mapper.update:
UPDATE IM_REQEST SET
	PROGRS_SITTN_CODE = 'IM03000003'
WHERE 1=1

	AND REQEST_SEQNO IN(
		SELECT REQEST_SEQNO FROM RS_MHRLS_EDAY_CHCK_REQEST
		WHERE MHRLS_EDAY_CHCK_SEQNO = #{mhrlsEdayChckSeqno}
	)			


	AND REQEST_SEQNO = #{reqestSeqno}

UPDATE RS_MHRLS_EDAY_CHCK A SET
	A.DELETE_AT =

					'Y'


					(
						SELECT 
							CASE WHEN COUNT(*) > 0 THEN 'N' ELSE 'Y' END 
						FROM RS_MHRLS_EDAY_CHCK_REQEST IA 
						WHERE IA.MHRLS_EDAY_CHCK_SEQNO = #{mhrlsEdayChckSeqno}
					)

WHERE A.MHRLS_EDAY_CHCK_SEQNO = #{mhrlsEdayChckSeqno}

UPDATE RS_MHRLS_CL_DETECT_LIMIT SET
	 DELETE_AT = 'Y'
	, LAST_CHANGER_ID = #{lastChangerId}
	, LAST_CHANGE_DT = SYSDATE
WHERE MHRLS_CL_CODE = #{mhrlsClCode}	
AND YEAR = #{year}
AND QU = #{qu}
