mapper.select:
WITH T AS(SELECT MENU_SEQNO, UPPER_MENU_SEQNO, MENU_NM,MNL_CN, MENU_DC, USE_AT, SORT_ORDR, MENU_URL , MNL_USE_AT, CTMMNY_OTHBC_AT, level menu_Lv, LAST_CHANGER_ID, LAST_CHANGE_DT FROM SY_MENU SY WHERE USE_AT = 'Y'
AND MENU_SEQNO = #{menuSeqno}
AND MENU_URL = #{menuUrl}
CONNECT BY PRIOR SY.MENU_SEQNO = SY.UPPER_MENU_SEQNO START WITH SY.UPPER_MENU_SEQNO IS NULL ORDER SIBLINGS BY SORT_ORDR)
SELECT * FROM T, (SELECT MENU_SEQNO FROM SY_MENU WHERE USE_AT = 'Y' START WITH
MENU_NM LIKE '%' || #{menuNmSearch} || '%'
MENU_SEQNO = #{menuTwo}
MENU_SEQNO = #{menuOne}
UPPER_MENU_SEQNO IS NULL
CONNECT BY PRIOR MENU_SEQNO = UPPER_MENU_SEQNO GROUP BY MENU_SEQNO UNION SELECT MENU_SEQNO FROM SY_MENU WHERE USE_AT = 'Y' START WITH
MENU_NM LIKE '%' || #{menuNmSearch} || '%'
MENU_SEQNO = #{menuTwo}
MENU_SEQNO = #{menuOne}
UPPER_MENU_SEQNO IS NULL
CONNECT BY PRIOR UPPER_MENU_SEQNO = MENU_SEQNO GROUP BY MENU_SEQNO ) M WHERE T.MENU_SEQNO = M.MENU_SEQNO

mapper.insert:
	INSERT INTO SY_MENU (
 		MENU_SEQNO
 		,UPPER_MENU_SEQNO
 		,MENU_NM
 		,MENU_DC
 		,USE_AT
 		,SORT_ORDR
 		,MENU_URL
 		,MNL_USE_AT
 		,CTMMNY_OTHBC_AT
 		,LAST_CHANGER_ID
 		,LAST_CHANGE_DT
	) VALUES (
		SQ_SY_MENU.NEXTVAL
		,#{upperMenuSeqno}
		,#{menuNm}
		,#{menuDc}
		,#{useAt}
		,(SELECT NVL(MAX(SORT_ORDR),0)+1 FROM SY_MENU WHERE UPPER_MENU_SEQNO = #{upperMenuSeqno})
		,#{menuUrl}
		,#{mnlUseAt}
		,#{ctmmnyOthbcAt}
		,
		,SYSDATE
	)

mapper.update:
UPDATE SY_MENU SET
	MENU_SEQNO        = #{menuSeqno}
	,UPPER_MENU_SEQNO = #{upperMenuSeqno}
	,MENU_NM          = #{menuNm}
	,MENU_DC          = #{menuDc}
	,USE_AT           = #{useAt}
	,SORT_ORDR        = #{sortOrdr}
	,MENU_URL         = #{menuUrl}
	,MNL_USE_AT       = #{mnlUseAt}
	,CTMMNY_OTHBC_AT  = #{ctmmnyOthbcAt}
	,LAST_CHANGER_ID  = 
	,LAST_CHANGE_DT   = SYSDATE
WHERE MENU_SEQNO = #{menuSeqno}

UPDATE SY_MENU SET
	MNL_CN = 'CLOB TO BLOB'
	,DATA = #{blobMnlCn}
	,LAST_CHANGER_ID = '${@lims.util.GetUserSession@getUserId()}'
	,LAST_CHANGE_DT = SYSDATE
WHERE MENU_SEQNO = #{menuSeqno}

mapper.select:
SELECT
	A.DATA AS BLOB_MNL_CN,
	MENU_NM
FROM SY_MENU A
WHERE A.MENU_SEQNO =  #{menuSeqno}
AND MENU_URL = #{menuUrl}

SELECT COUNT(A.MENU_SEQNO) FROM SY_MENU_BKMK A
WHERE A.USER_ID = 
  AND A.MENU_SEQNO = #{menuSeqno}

mapper.delete:
DELETE FROM SY_MENU_BKMK
WHERE USER_ID = TO_NUMBER()
  AND MENU_SEQNO = #{menuSeqno}
