mapper.select:
SELECT '' AS CHRG_TEAM_SEQNO , A.USER_ID AS TEAM_CHARGER_ID , A.USER_NM AS TEAM_CHARGER_NM , C.INSPCT_INSTT_NM AS DEPT_NM FROM SY_USER A INNER JOIN SY_INSPCT_INSTT C ON A.DEPT_CODE = C.INSPCT_INSTT_CODE LEFT OUTER JOIN ( SELECT * FROM SY_CHRG_TEAM_USER IA WHERE IA.CHRG_TEAM_SEQNO = #{chrgTeamSeqno} ) B ON A.USER_ID = B.TEAM_CHARGER_ID WHERE A.USE_AT = 'Y' AND B.CHRG_TEAM_SEQNO IS NULL
AND A.USER_NM LIKE '%'|| #{userNmSearch} ||'%'
AND C.MMNY_SE_CODE = #{mmnySeCode}
AND C.INSPCT_INSTT_CODE = #{deptCodeSearch}
AND C.LIMS_USE_AT = #{limsUseAtSch}
AND C.ANAL_AT = #{analsAtSch}
ORDER BY C.INSPCT_INSTT_NM, A.USER_NM

mapper.insert:

	SELECT SQ_SY_CHRG_TEAM.NEXTVAL AS chrgTeamSeqno 
	FROM DUAL


INSERT INTO SY_CHRG_TEAM
(
	CHRG_TEAM_SEQNO
	,DEPT_CODE
	,CHRG_TEAM_NM
	,RM		
	,RCEPT_PC_IP
	,ABRV
	,USE_AT
	,LAST_CHANGER_ID
	,LAST_CHANGE_DT
)
VALUES(
	#{chrgTeamSeqno}

	,3992
	,#{chrgTeamNm}
	,#{rm}	
	,#{rceptPcIp}	
	,#{abrv}
	,#{useAt}
	,#{lastChangerId}
	,SYSDATE
)

mapper.update:

UPDATE SY_CHRG_TEAM SET
	DEPT_CODE = '3992'
	,CHRG_TEAM_NM = #{chrgTeamNm}
	,ABRV = #{abrv}
	,RCEPT_PC_IP = #{rceptPcIp}
	,RM = #{rm}
	,USE_AT = #{useAt}
	,LAST_CHANGER_ID = #{lastChangerId}
	,LAST_CHANGE_DT = SYSDATE
WHERE CHRG_TEAM_SEQNO = #{chrgTeamSeqno}

mapper.insert:
INSERT INTO SY_CHRG_TEAM_USER
(
	CHRG_TEAM_SEQNO
	,TEAM_CHARGER_ID	
	,TIMHDER_AT	
	,LAST_CHANGER_ID
	,LAST_CHANGE_DT
) VALUES(
	#{chrgTeamSeqno}
	,#{teamChargerId}
	,CASE WHEN #{timhderAt} = 'true' THEN 'Y' ELSE 'N' END
	,#{lastChangerId}
	,SYSDATE
)

mapper.update:
UPDATE SY_CHRG_TEAM
SET
	DELETE_AT = 'Y'
	, LAST_CHANGER_ID = #{lastChangerId}
	, LAST_CHANGE_DT = SYSDATE
WHERE CHRG_TEAM_SEQNO = #{chrgTeamSeqno}

mapper.insert:
INSERT INTO SY_CHRG_TEAM_IP(
	 CHRG_TEAM_IP_SEQNO
	,CHRG_TEAM_SEQNO
	,CHRG_TEAM_SE_NM
	,RCEPT_PC_IP
	,LAST_CHANGER_ID
	,LAST_CHANGE_DT
) VALUES (
	SQ_SY_CHRG_TEAM_IP.NEXTVAL
	,#{chrgTeamSeqno}
	,#{chrgTeamSeNm}
	,#{rceptPcIp}
	,
	,SYSDATE
)

mapper.update:
UPDATE SY_CHRG_TEAM_IP SET
	CHRG_TEAM_SE_NM = #{chrgTeamSeNm}
	,RCEPT_PC_IP = #{rceptPcIp}
	,LAST_CHANGER_ID = 
	,LAST_CHANGE_DT = SYSDATE
WHERE CHRG_TEAM_IP_SEQNO = #{chrgTeamIpSeqno}
AND CHRG_TEAM_SEQNO = #{chrgTeamSeqno}

mapper.delete:
DELETE FROM SY_CHRG_TEAM_IP
WHERE CHRG_TEAM_IP_SEQNO = #{chrgTeamIpSeqno}
AND CHRG_TEAM_SEQNO = #{chrgTeamSeqno}
